package Topic1;

public class StaticMethod {
	public static void myName()
	{
		System.out.println("Name = Dipesh Khotele");
	}
	public static void address()
	{
		System.out.println("Address = Rajani Nagar Khat road,Bhandara");
	}
	public static void state()
	{
		System.out.println("State = MAHARASHTRA");
	}
	public static void pincode()
	{
		System.out.println("Pincode = 441 904");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
       StaticMethod.myName();
       StaticMethod.address();
       StaticMethod.state();
       StaticMethod.pincode();
	
	}

}
