package TypesOfMethod;

public class Type1 { //To Call a Method Within Class
	
	public static void method1()
	{
		System.out.println("My Name is DK");
	}
	public void method2()
	{
		System.out.println("JAWAN");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Type1.method1();
		Type1 xyz = new Type1();
		xyz.method2();
		

	}

}
